mapper.select:
select A.MENU_ID, A.MENU_NAME, A.MENU_URL, A.UPPER_MENU_ID, A.MENU_ORDER, A.CRE_DT, A.CRE_USER, A.MOD_DT, A.MOD_USER from WISETM_MENU_INFO A LEFT OUTER JOIN WISETM_USERAUTH_MENU B ON A.MENU_ID = B.MENU_ID where (A.UPPER_MENU_ID is null or A.UPPER_MENU_ID = '')
and B.USER_AUTH = #{userAuth}
order by A.MENU_ORDER, A.MENU_NAME

select A.MENU_ID, A.MENU_NAME, A.MENU_URL, A.UPPER_MENU_ID, A.MENU_ORDER, A.CRE_DT, A.CRE_USER, A.MOD_DT, A.MOD_USER from WISETM_MENU_INFO A LEFT OUTER JOIN WISETM_USERAUTH_MENU B ON A.MENU_ID = B.MENU_ID where A.UPPER_MENU_ID = #{upperMenuId}
and B.USER_AUTH = #{userAuth}
order by A.MENU_ORDER, A.MENU_NAME

select count(*)
from WISETM_USERAUTH_MENU
where MENU_ID = #{menuId}

mapper.insert:
insert into WISETM_USERAUTH_MENU(
    MENU_ID,
    USER_AUTH
)
values(
    #{userAuthMenu},
    #{userAuth}
)

mapper.delete:
delete from WISETM_USERAUTH_MENU
where MENU_ID = #{userAuthMenu}
and USER_AUTH = #{userAuth}
